const countingSort = (arSize, divSizes, divs, div_update) => {

  let max = Math.max(...divSizes);

  let count = new Array(max + 1).fill(0);

  for (let i = 0; i < arSize; i++) {
    count[divSizes[i]]++;
  }

  for (let i = 1; i <= max; i++) {
    count[i] += count[i-1];
  }

   for (let i = 0; i < count.length; i++) {
    while (count[i] > 0) {
        div_update(divs[i], divSizes[i], "green");

        count[i]--;
    }
  }

};
  
export default countingSort;
