import { cloneElement } from "react";

const bubbleSort = (arSize, divSizes, divs, speed, setDivs, div_update) => {
  var delay = 10000 / (Math.floor(arSize / 10) * speed); //Decrease numerator to increase speed.

  for (var i = 0; i < arSize - 1; i++) {
    for (var j = 0; j < arSize - i - 1; j++) {
      div_update(divs[j], divSizes[j], "yellow", arSize, setDivs, delay); //Color update

      if (divSizes[j] > divSizes[j + 1]) {
        div_update(divs[j], divSizes[j], "red", arSize, setDivs, delay); //Color update
        div_update(divs[j + 1], divSizes[j + 1], "red", arSize, setDivs, delay); //Color update

        var temp = divSizes[j];
        divSizes[j] = divSizes[j + 1];
        divSizes[j + 1] = temp;

        div_update(divs[j], divSizes[j], "red", arSize, setDivs, delay); //Height update
        div_update(divs[j + 1], divSizes[j + 1], "red", arSize, setDivs, delay); //Height update
      }
      div_update(divs[j], divSizes[j], "blue", arSize, setDivs, delay); //Color updat
    }
    div_update(divs[j], divSizes[j], "green", arSize, setDivs, delay); //Color update
  }
  div_update(divs[0], divSizes[0], "green", arSize, setDivs, delay); //Color update
};

export default bubbleSort;
