import "./App.css";
import { cloneElement, useEffect, useRef, useState } from "react";
import bubbleSort from "./sorts/bubble";
import insertionSort from "./sorts/insertion";
import selectionSort from "./sorts/selection";
import quickSort from "./sorts/quick";

function App() {
  const inpAs = useRef();
  const speed = 500;
  const [timeWorst, setTimeWorst] = useState();
  const [timeAverage, setTimeAverage] = useState();
  const [timeBest, setTimeBest] = useState();
  const [divSizes, setDivSizes] = useState([]);
  const [divs, setDivs] = useState([]);

  const [arSize, setArSize] = useState(20);

  useEffect(() => {
    const generateDivSizes = () => {
      const newDivSizes = [];
      const newDivs = [];
      for (let i = 0; i < arSize; i++) {
        const size = Math.floor(Math.random() * 0.5 * (80 - 20)) + 10;
        newDivSizes.push(size);

        const div = (
          <div
            key={i}
            style={{
              margin: `0% ${0.1}%`,
              backgroundColor: "blue",
              width: `${100 / arSize - 2 * 0.1}%`,
              height: `${size}%`,
            }}
          ></div>
        );
        newDivs.push(div);
      }

      setDivSizes(newDivSizes);
      setDivs(newDivs);
    };

    generateDivSizes();
  }, [arSize, inpAs]);

  var delay_time = 10000 / (Math.floor(arSize / 10) * speed);
  var c_delay = 0;

  function div_update(cont, height, color) {
    window.setTimeout(function () {
      console.log(cont);

      const updatedStyle = {
        margin: `0% ${0.1}%`,
        width: `${100 / arSize - 2 * 0.1}%`,
        height: `${height}%`,
        backgroundColor: color,
      };

      setDivs((prevDivs) => {
        const newDivs = [...prevDivs];
        const index = newDivs.findIndex((div) => div.key === cont.key);
        if (index !== -1) {
          newDivs[index] = cloneElement(newDivs[index], {
            style: updatedStyle,
          });
        }
        return newDivs;
      });
    }, (c_delay += delay_time));
  }


  const quickPartition = (start, end) => {
    var i = start + 1;
    var piv = divSizes[start]; //make the first element as pivot element.
    div_update(divs[start], divSizes[start], "yellow"); //Color update

    for (var j = start + 1; j <= end; j++) {
      if (divSizes[j] < piv) {
        div_update(divs[j], divSizes[j], "yellow"); //Color update

        div_update(divs[i], divSizes[i], "red"); //Color update
        div_update(divs[j], divSizes[j], "red"); //Color update

        let temp = divSizes[i];
        divSizes[i] = divSizes[j];
        divSizes[j] = temp;

        div_update(divs[i], divSizes[i], "red"); //Height update
        div_update(divs[j], divSizes[j], "red"); //Height update

        div_update(divs[i], divSizes[i], "blue"); //Height update
        div_update(divs[j], divSizes[j], "blue"); //Height update

        i += 1;
      }
    }
    div_update(divs[start], divSizes[start], "red"); //Color update
    div_update(divs[i - 1], divSizes[i - 1], "red"); //Color update

    let temp = divSizes[start]; //put the pivot element in its proper place.
    divSizes[start] = divSizes[i - 1];
    divSizes[i - 1] = temp;

    div_update(divs[start], divSizes[start], "red"); //Height update
    div_update(divs[i - 1], divSizes[i - 1], "red"); //Height update

    for (var t = start; t <= i; t++) {
      div_update(divs[t], divSizes[t], "green"); //Color update
    }

    return i - 1; //return the position of the pivot
  };

  const quickSort = (start, end) => {

    if (start < end) {
      //stores the position of the pivot element
      var piv_pos = quickPartition(start, end);
      quickSort(start, piv_pos - 1); //sorts the left side of pivot.
      quickSort(piv_pos + 1, end); //sorts the right side of pivot.
    }
  };

  const onBubbleSort = () => {
    setTimeWorst();
    setTimeAverage();
    setTimeBest();

    bubbleSort(arSize, divSizes, divs, div_update);
  };

  const onInsertionSort = () => {
    setTimeWorst();
    setTimeAverage();
    setTimeBest();

    insertionSort(arSize, divSizes, divs, div_update);
  };

  const onSelectionSort = () => {
    setTimeWorst();
    setTimeAverage();
    setTimeBest();

    selectionSort(arSize, divSizes, divs, div_update);
  };

  // const onQuickSort = () => {
  //   setTimeWorst();
  //   setTimeAverage();
  //   setTimeBest();

  //   quickSort(divSizes, divs, div_update, 0, arSize-1);
  // };

  return (
    <>
      <header>
        <nav>
          <div class="array-inputs">
            <p>Size of the array:</p>

            <button onClick={() => setArSize(20)}>Small Array</button>
            <button onClick={() => setArSize(80)}>Large Array</button>
            <button id="a_generate">Generate New Array!</button>
          </div>

          <div class="header_right">
            <p class="nav-heading">Sorting Visualizer</p>

            <div class="algos">
              <button onClick={() => onBubbleSort()}>Bubble</button>
              <button onClick={() => onInsertionSort()}>Insertion</button>
              <button onClick={() => onSelectionSort()}>Selection</button>
              <button onClick={() => quickSort()}>Quick </button>
            </div>
          </div>
        </nav>
      </header>

      <section>
        <div id="Info_Cont1">
          <h3>TIME COMPLEXITY</h3>

          <div
            class="Complexity_Containers"
            id="Time_Complexity_Types_Container"
          >
            <div class="Pair_Definitions">
              <p class="Sub_Heading">Worst case:</p>
              <p id="Time_Worst">{timeWorst}</p>
            </div>

            <div class="Pair_Definitions">
              <p class="Sub_Heading">Average case:</p>
              <p id="Time_Average">{timeAverage}</p>
            </div>

            <div class="Pair_Definitions">
              <p class="Sub_Heading">Best case:</p>
              <p id="Time_Best">{timeBest}</p>
            </div>
          </div>
        </div>

        <div id="array_container">{divs}</div>

        <div id="Info_Cont2">
          <h3>SPACE COMPLEXITY</h3>

          <div
            class="Complexity_Containers"
            id="Space_Complexity_Types_Container"
          >
            <div class="Pair_Definitions">
              <p class="Sub_Heading">Worst case:</p>
              <p id="Space_Worst"></p>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}

export default App;
